import React from 'react';
import ExpenseDateComponent from './ExpenseDateComponents';
import './ExpenseItem.css'
function ExpenseItem(props) {

    /* 
    first it was harded code in the prop like below
        const expenseDate = new Date(2021, 2, 28);
        const expenseItem = 'Car Insurance';
        const expenseAmount = 2987; */

    /*  second we defined the componet for this nested component outside
    const month = props.date.toLocaleString('en-US', { month: 'long' });
     const day = props.date.toLocaleString('en-US', { day: '2-digit' })
     const year = props.date.getFullYear(); */
    return (
        <div className='expense-item'>
            {/* <div>
                <div>
                    {month}
                </div>
                <div>
                    {year}
                </div>
                <div>
                    {day}
                </div>
            </div> */}

            <ExpenseDateComponent></ExpenseDateComponent>
            <div className='expense-item__description'>
                <h2>
                    {props.title}
                </h2>
            </div>

            <div className='expense-item__price'>
                ${props.amount}
            </div>



        </div>
    );
};

export default ExpenseItem;
